mapper.update:
update t_student
set name = #{name,jdbcType=VARCHAR},
  price = #{price,jdbcType=DOUBLE},
  address = #{address,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, price, address, phone
from t_student
where id = #{id,jdbcType=VARCHAR}
